import { Component } from "react";
import TableData from "../Components/TableData";
import "./index.css";

const apiStatusConstants = {
  initial: "INITIAL",
  success: "SUCCESS",
  failed: "FAILED",
};

class UserTable extends Component {
  state = { userData: [], loading: apiStatusConstants.initial };

  componentDidMount = () => {
    this.getUserDataFromApi();
  };

  getUserDataFromApi = async () => {
    const apiUrl = "https://gorest.co.in/public/v1/users";
    const options = {
      Method: "GET",
      Headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Authorization:
          "Bearer 5d820860359195f5be7dcd70ce78bcd8e95d5f5042978d16983766a143808c36",
      },
    };
    const response = await fetch(apiUrl);
    const result = await response.json();
    console.log(result.data);
    this.setState({
      userData: result.data,
      loading: apiStatusConstants.success,
    });
  };

  render() {
    const { userData } = this.state;
    return (
      <>
        <div className="container">
          <div className="row">
            <div className="col-12 text-center">
              <h1 className="pb-4">User Table</h1>

              {/* table  */}

              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">ID</th>
                    <th scope="col">Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Gender</th>
                    <th scope="col">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {userData.map((user) => (
                    <TableData key={user.id} user={user} />
                  ))}
                </tbody>
              </table>
            </div>

            <Link to="/">Go Back</Link>
          </div>
        </div>
      </>
    );
  }
}

export default UserTable;
