import { Link } from "react-router-dom";
import TableData from "../Components/TableData";
import StatisticsWidgets from "../Components/StatisticsWidgets/StatisticsWidgets";
import "./index.css";

const apiStatusConstants = {
  initial: "INITIAL",
  success: "SUCCESS",
  failed: "FAILED",
};

class UserTable extends Component {
  state = { userData: [], loading: apiStatusConstants.initial, cardInfo: {totalUsers:0,
    totalMales:0,
    totalFemales:0,
   totalActive:0} };

  componentDidMount = () => {
    this.getUserDataFromApi();
  };

  getUserDataFromApi = async () => {
    const apiUrl = "https://gorest.co.in/public/v1/users";
    const options = {
      Method: "GET",
      Headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Authorization:
          "Bearer 5d820860359195f5be7dcd70ce78bcd8e95d5f5042978d16983766a143808c36",
      },
    };
    const response = await fetch(apiUrl);
    const result = await response.json();
    console.log(result.data);
    this.setState({
      userData: result.data,
      loading: apiStatusConstants.success,
    });
  };

  setCardInfo=()=> {
      const {userInfo,userData}=this.state
      const {totalUsers,
        totalMales,
        totalFemales,
       totalActive}= userInfo

      const totalMalesCount= userData.map(eachItem=> {
      if(eachItem.gender==='male') {
      let count=0;

      count=count+1;
      return count

}
})

const totalFemalesCount= userData.map(eachItem=> {
    if(eachItem.gender==='female') {
    let count=0;

    count=count+1;
    return count

}
})

const totalActiveCount= userData.map(eachItem=> {
    if(eachItem.status==='active') {
    let count=0;

    count=count+1;
    return count

}
})



  }



  render() {
    const { userData } = this.state;
    return (
      <>
        <div className="container">
          <div className="row pt-4">
              <div className="col-12 mb-3">
              <Link to="/">Go Back</Link>

              </div>
<div className="row mb-4">
    <div className="col-12 col-lg-3">
<StatisticsWidgets infoName="Users" info="2333"/>
    </div>
    <div className="col-12 col-lg-3">
<StatisticsWidgets infoName="Males" info="2333"/>
    </div>
    <div className="col-12 col-lg-3">
<StatisticsWidgets infoName="Females" info="2333"/>
    </div>
    <div className="col-12 col-lg-3">
<StatisticsWidgets infoName="Active" info="2333"/>
    </div>

</div>

              <div className="col-3">
                <h1 className="pb-4">User Table</h1>

                </div>
                <div className="col-6"></div>
                <div className="col-3 text-end">
                    <button type="button" className="btn btn-success">Add User</button>
                </div>
            <div className="col-12 text-center">


              {/* table  */}

              <table className="table table-success table-striped ">
                <thead>
                  <tr>
                    <th scope="col">ID</th>
                    <th scope="col">Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Gender</th>
                    <th scope="col">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {userData.map((user) => (
                    <TableData key={user.id} user={user} />
                  ))}
                </tbody>
              </table>
            </div>


          </div>
        </div>
      </>
    );
  }
}

export default UserTable;
